mapper.insert:
/* 입력 */
INSERT INTO
mushroom_past_data
    (past_data_idx
    , username
    , company_info_idx
    , crop_idx
    , past_data_mushroom_production
    , past_data_total_revenue
    , past_data_badge_use
    , past_data_year
    , past_data_use
    , past_data_comparison
    , past_data_date)

    VALUES (
    #{pastDataIdx}
    , #{username}
    , #{companyInfoIdx}
    , #{cropIdx}
    , #{pastDataMushroomProduction}
    , #{pastDataTotalRevenue}
    , #{pastDataBadgeUse}
    , #{pastDataYear}
    , '사용'
    , #{pastDataComparison}
    , curdate()
    )

mapper.delete:
/* 삭제*/
 DELETE
 FROM
      mushroom_past_data
 WHERE
      past_data_idx=#{pastDataIdx}
